using UnityEngine;\n\npublic class InputManager : MonoBehaviour {\n    // Variables for different input types\n    private Vector2 moveInput;\n    public float moveSpeed = 5f;\n    public Camera mainCamera;\n\n    void Update() {\n        HandleMovement();\n        HandleKicking();\n        HandlePassing();\n        HandleSprinting();\n    }\n\n    void HandleMovement() {\n        // Keyboard Input\n        moveInput.x = Input.GetAxis("Horizontal"); // A/D or Left/Right\n        moveInput.y = Input.GetAxis("Vertical"); // W/S or Up/Down\n        // Gamepad Input\n        if (Input.GetJoystickNames().Length > 0) {\n            moveInput.x = Input.GetAxis("JoyStickHorizontal");\n            moveInput.y = Input.GetAxis("JoyStickVertical");\n        }\n        // Mobile Touch Input\n        if (Input.touchCount > 0) {\n            Touch touch = Input.GetTouch(0);\n            if (touch.phase == TouchPhase.Moved) {\n                moveInput = touch.deltaPosition;\n                moveInput.Normalize();\n            }\n        }\n\n        // Move player based on inputs\n        Vector3 movement = new Vector3(moveInput.x, 0f, moveInput.y) * moveSpeed * Time.deltaTime;\n        transform.Translate(movement);\n    }\n\n    void HandleKicking() {\n        if (Input.GetButtonDown("Fire1")) { // Mouse Button 0 or Controller Button 0\n            Debug.Log("Kick Action");\n        }\n    }\n\n    void HandlePassing() {\n        if (Input.GetButtonDown("Fire2")) { // Mouse Button 1 or Controller Button 1\n            Debug.Log("Pass Action");\n        }\n    }\n\n    void HandleSprinting() {\n        if (Input.GetButton("Fire3")) { // Mouse Button 2 or Controller Button 2\n            moveSpeed = 10f;\n        } else {\n            moveSpeed = 5f;\n        }\n    }\n\n    // Optional: Method to rotate camera based on mouse or touch inputs\n    void HandleCameraControl() {\n        // Camera controls can involve mouse or touch movement\n        if (Input.GetMouseButton(1)) { // Right mouse button\n            float horizontal = Input.GetAxis("Mouse X");\n            float vertical = Input.GetAxis("Mouse Y");\n            mainCamera.transform.Rotate(-vertical, horizontal, 0f);\n        }\n    }\n}